@st.cache_resource
def load_models():
    model = joblib.load("./Model/autism_model.pkl")
    scaler = joblib.load("./Model/autism_scaler.pkl")
    sentiment_analyzer = pipeline("sentiment-analysis", model="distilbert-base-uncased-finetuned-sst-2-english")
    similarity_model = SentenceTransformer('all-MiniLM-L12-v2')

    return model, scaler, sentiment_analyzer, similarity_model
model, scaler, sentiment_analyzer, similarity_model = load_models()

