def extract_keywords(user_input):
 
    keywords = re.findall(r'\b\w+\b', user_input.lower())   
    return " ".join(keywords)  
def fuzzy_match_response(user_input, mapping_dict, error_message):
    user_input = extract_keywords(user_input)
    user_embedding = similarity_model.encode(user_input, convert_to_tensor=True)
    best_match = None
    max_similarity = 0.0
    for key_phrase, mapped_value in mapping_dict.items():
        phrase_embedding = similarity_model.encode(key_phrase, convert_to_tensor=True)
        similarity = util.pytorch_cos_sim(user_embedding, phrase_embedding).item()
        if similarity > max_similarity:
            max_similarity = similarity
            best_match = mapped_value
    if max_similarity > 0.55:
        return best_match, None
    for key_phrase, mapped_value in mapping_dict.items():
        if fuzz.partial_ratio(key_phrase.lower(), user_input.lower()) > FUZZY_MATCH_THRESHOLD:
            return mapped_value, None
    return None, error_message
