def is_question(text):
    text = text.strip()
    if text.endswith('?'):
        return True
    interrogative_words = set([
        "do", "does", "did", "is", "are", "can", "could", "will", "would", "should",
        "ما", "هل", "كيف", "ماذا", "أين", "لماذا", "من"
    ])
    tokens = text.lower().split()
    if tokens and tokens[0] in interrogative_words:
        return True
    return False
def is_answer_indicating_ability(user_input, question=None):
    if is_question(user_input):
        return False

    user_input = user_input.strip().lower()

    if question and question in indicative_keywords_per_question:
        if user_input in [kw.lower() for kw in indicative_keywords_per_question[question]]:
            return True


    if re.search(r"\b(he|she|child)\s+(do|does|did)\b", user_input, re.IGNORECASE):
        return True

    ability_keywords = [
        r"\bcan\b", r"\bable\b", r"\bcapable\b",
        r"\bcannot\b", r"\bcan't\b", r"\bwon't\b"
    ]
    for pattern in ability_keywords:
        if re.search(pattern, user_input, re.IGNORECASE):
            return True

    return False
   
