def normalize_input(text):
    text = text.strip().lower()
    if not re.search(r'[a-zA-Zا-ي]', text):
        return text
    try:
        if detect(text) == "ar":
            text = translate_arabic_to_english(text)
    except Exception as e:
        st.error("Language detection failed. Please try again.")
        logging.error("Language detection error: " + str(e))
        return None
    text = correct_spelling(text)
    return text

    
def parse_age_input(text):
    text = text.lower()
    years = 0
    months = 0

    number_words = {
        "zero": 0, "one": 1, "two": 2, "three": 3, "four": 4,
        "five": 5, "six": 6, "seven": 7, "eight": 8, "nine": 9,
        "ten": 10, "eleven": 11, "twelve": 12, "thirteen": 13,
        "fourteen": 14, "fifteen": 15, "sixteen": 16, "seventeen": 17,
        "eighteen": 18, "nineteen": 19, "twenty": 20, "thirty": 30,
        "forty": 40, "fifty": 50, "sixty": 60, "seventy": 70,
        "eighty": 80, "ninety": 90, "hundred": 100
    }

    year_matches = re.findall(r'(\d+)\s*(year|years|yr|yrs)', text)
    for match in year_matches:
        try:
            years += int(match[0])
        except:
            pass

    month_matches = re.findall(r'(\d+)\s*(month|months|mo|mos)', text)
    for match in month_matches:
        try:
            months += int(match[0])
        except:
            pass

    if not year_matches and not month_matches:
        match = re.search(r'\d+', text)
        if match:
            num = int(match.group())
        else:
            
            tokens = text.split()
            num = None
            for token in tokens:
                if token in number_words:
                    num = number_words[token]
                    break
            if num is None:
                return None, "Please enter a valid age ."
        
        if num <= 10:
            years = num
        else:
            months = num

    total_months = years * 12 + months
    if 0 <= total_months <= 84:
        return total_months, None
    else:
        return None, "Please enter a valid age ."
